document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".tab-container").forEach((t=>{const e=t.querySelectorAll(".tablinks"),c=t.querySelectorAll(".tabcontent");e.forEach((n=>{n.addEventListener("click",(function(n){c.forEach((t=>{t.style.display="none"})),e.forEach((t=>t.classList.remove("active")));const o=this.dataset.target;t.querySelector(`#${o}`).style.display="block",this.classList.add("active")}))}));const n=t.querySelector(".tablinks");n&&n.click()}))}));